INSERT INTO `faqs` VALUES (1,'Jam Operasional Inkindo Yogyakarta','Inkindo Yogyakarta beroperasi setiap hari dengan jam kerja mulai pukul 8 pagi hingga 4 sore. Kami siap membantu Anda selama jam operasional tersebut untuk memenuhi kebutuhan dan pertanyaan Anda',1,NULL,NULL),(2,'Dimana Lokasi Inkindo Yogyakarta','Kantor Inkindo Yogyakarta terletak di Jl. Godean KM. 5 Gg. Nogosaren Baru No. 32, Banyuraden, Gamping, Sleman, Yogyakarta 55293. Lokasi strategis ini memudahkan akses bagi masyarakat untuk mendapatkan layanan Inkindo. Jangan ragu untuk mengunjungi kami selama jam operasional pukul 8 pagi hingga 4 sore, Senin hingga Jumat.',1,NULL,NULL);
