CREATE TABLE `layanan_non_konstruksis` (
  `id` bigint(20) unsigned NOT NULL,
  `jenis_layanan_non_konstruksi` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `deskripsi_layanan_non_konstruksi` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `klasifikasi_non_konstruksi_id` bigint(20) unsigned NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
/*!40101 SET character_set_client = @saved_cs_client */;

--
