INSERT INTO `program_kerja` VALUES (1,'Memimpin seluruh jajaran organisasi sesuai Anggaran Dasar, Anggaran Rumah Tangga, Kode Etik, Standar, dan Pedoman Profesi.',1,NULL,NULL),(2,'Bertanggungjawab melaksanakan agenda organisasi 4 tahunan dan agenda organisasi sesuai Garis Besar Haluan Kebijakan Organisasi dan Rancangan Anggaran Pendapatan Belanja Organisasi.',1,NULL,NULL),(3,'Mengkoordinasikan serta membantu semua Wakil Ketua agar tercapai keterpaduan dalam pelaksanaan kegiatan organisasi.',2,NULL,NULL),(4,'Melaporkan tugas-tugasnya secara berkala dan bertanggung jawab kepada Ketua.',2,NULL,NULL),(5,'Bersama Ketua dan Bendahara mengkoordinir pelaksanaan rapat kerja.',2,NULL,NULL),(6,'Bersama Ketua menandatangani surat menyurat.',2,NULL,NULL),(7,'Bertanggung jawab dalam pengelolaan keuangan organisasi.',3,NULL,NULL),(8,'Bersama Wakil Ketua I mencari sumber keuangan untuk pendanaan kegiatan organisasi.',3,NULL,NULL),(9,'Melaporkan tugas-tugasnya secara berkala dan bertanggung jawab kepada Ketua.',3,NULL,NULL),(10,'Membuat laporan keuangan organisasi yang dilaporkan pada saat Rapat Kerja.',3,NULL,NULL),(11,'Menjalin, membina serta memantapkan hubungan kerjasama dengan pihak stakeholder (instansi pemerintah dan swasta) serta mitra kerja (perusahaan swasta).',4,NULL,NULL),(12,'Meningkatkan kerjasama dengan DPP INKINDO provinsi lain untuk kepentingan anggota',4,NULL,NULL),(13,'Membina hubungan dan meningkatkan kerjasama dengan Asosiasi Profesi dan Asosiasi Badan Usaha lainnya',4,NULL,NULL),(14,'Menciptakan kegiatan berkesinambungan yang berlandaskan kode etik profesional',4,NULL,NULL),(15,'Menjalin hubungan kerjasama antar anggota dan penguatan pembinaan anggota.',5,NULL,NULL),(16,'Melakukan pembinaan, peningkatan kemampuan, ketrampilan, dan integritas kepribadian anggota.',5,NULL,NULL),(17,'Mengadakan kegiatan pertemuan kepada Perwakilan Organisasi Provinsi (POP) secara periodik.',5,NULL,NULL),(18,'Menciptakan iklim hubungan harmonis antar anggota.',5,NULL,NULL),(19,'Mengadakan kajian dan usulan strategis yang berhubungan dengan isu-isu terbaru untuk kepentingan anggota.',6,NULL,NULL),(20,'Mengadakan kegiatan pelatihan atau workshop mengenai perencanaan strategis organisasi dan perusahaan.',6,NULL,NULL),(21,'Membuat kajian terkait dengan billing rate dan pajak.',6,NULL,NULL),(22,'Melakukan pendalaman dan membuat resume terhadap peraturan yang berkaitan dengan jasa konstruksi.',7,NULL,NULL),(23,'Mengkompilasi semua peraturan yang melekat dalam pelaksanaan jasa konstruksi kepada anggota.',7,NULL,NULL),(24,'Melakukan kegiatan berkaitan dengan sosialisasi peraturan dengan jasa konstruksi ter-update kepada anggota.',7,NULL,NULL),(25,'Melakukan program berkesinambungan yang berhubungan dengan kepranataan kepada anggota.',7,NULL,NULL),(26,'Menciptakan sistem digitalisasi yang berkaitan dengan sistem informasi jasa konstruksi INKINDO.',8,NULL,NULL),(27,'Menjalankan dan mengembangkan website INKINDO DIY.',8,NULL,NULL),(28,'Mengadakan kegiatan sosialisasi terkait dengan Sistem Informasi yang berkaitan dengan jasa konstruksi (Sistem informasi database Tenaga Ahli).',8,NULL,NULL),(29,'Menciptakan dan membuat program sistem informasi yang menguatkan anggota.',8,NULL,NULL),(30,'Membuat database stakeholder dan mitra kerja bekerjasama dengan Wakil Ketua 1 untuk kepentingan anggota.',8,NULL,NULL),(31,'Mengkompilasi dan mensosialisasi pada anggota terkait dengan hukum yang akan berdampak kepada anggota.',9,NULL,NULL),(32,'Mengadakan pelatihan dan sosialisasi terkait dengan hukum jasa konstruksi dengan mengundang narasumber dari BPK, PidSus, PU, LKPP, dan lain-lain.',9,NULL,NULL),(33,'Mewujudkan fungsi hukum sebagai instrumen perlindungan bagi anggota.',9,NULL,NULL),(34,'Bekerjasama dengan Badan Advokasi Mediasi untuk melakukan pendampingan hukum yang berdampak kepada anggota.',9,NULL,NULL),(35,'Mengadakan kerjasama dengan perbankan dan asuransi.',10,NULL,NULL),(36,'Membuat sarana pelayanan yang bermanfaat untuk kesejahteraan dan produktivitas anggota.',10,NULL,NULL),(37,'Meneliti dan mengkaji usulan kenaikan gaji berkala untuk Badan Pelaksana diproses sesuai peraturan yang berlaku besama dengan Wakil Ketua 2.',10,NULL,NULL),(38,'Mengadakan kegiatan bakti sosial dengan melibatkan anggota yang bermanfaat untuk masyarakat luas.',11,NULL,NULL),(39,'Mengadakan kegiatan sayembara yang khusus diikuti oleh anggota.',11,NULL,NULL),(40,'Menciptakan kegiatan yang berkelanjutan dalam meningkatkan kreativitas anggota dalam bidang Ilmu Pengetahuan dan Teknologi.',11,NULL,NULL),(41,'Meningkatkan pelayanan kepada anggota melalui peningkatan dan perbaikan sarana prasarana.',12,NULL,NULL),(42,'Merencanakan dan melaksanakan program pengembangan dan pemeliharaan sarana prasarana.',12,NULL,NULL),(43,'Mengkoordinasikan dan mengawasi pemeliharaan, perbaikan, dan pengembangan sarana kantor kesekretariatan.',12,NULL,NULL),(44,'Melaksanakan inventarisasi barang/alat per unit kerja.',12,NULL,NULL),(45,'Menjalin dan memantapkan hubungan kerjasama dengan masyarakat.',13,NULL,NULL),(46,'Mengkoordinasikan seluruh kegiatan.',13,NULL,NULL),(47,'Melakukan penegakan serta pengawasan terhadap penataan Kode Etik dan Tata Laku Keprofesian.',14,NULL,NULL),(48,'Memberikan keputusan atas pelanggaran kode etik dan sanksi yang dilakukan anggota.',14,NULL,NULL),(49,'Menjalankan program penataran kode etik bagi anggota secara berkesinambungan.',14,NULL,NULL),(50,'Memberikan pertimbangan kepada DPP tentang arahan kebijakan dan program kerja organisasi.',15,NULL,NULL),(51,'Memberikan pertimbangan tentang pelaksanaan dan kesinambungan program kerja demi tercapainya tujuan dan keutuhan organisasi.',15,NULL,NULL),(52,'Menjaga keselarasan hubungan anggota organisasi di daerah.',16,NULL,NULL),(53,'Membantu DPP dalam pendistribusian informasi dan isu-isu penting bagi kepentingan anggota daerah.',16,NULL,NULL),(54,'Mengadakan pertemuan penting antar anggota daerah.',16,NULL,NULL),(55,'Sebagai fasilitator dan mediator antara DPP dengan Instansi Pemerintah.',16,NULL,NULL),(56,'Melakukan pendampingan terhadap anggota dalam kasus-kasus hukum yang berdampak pada anggota.',17,NULL,NULL),(57,'Memberikan advice hukum kepada anggota sebagai tindakan pencegahan kriminalisasi anggota dalam masalah jasa konstruksi.',17,NULL,NULL),(58,'Melayani anggota dalam proses Sertifikasi Badan Usaha (SBU) Jasa konsultansi Non Konstruksi bekerjasama dengan KADIN.',18,NULL,NULL),(59,'Memberikan bimbingan teknis anggota terhadap proses SBU secara periodik.',18,NULL,NULL);
